def is_palindrome(s):
 return(s == s[::-1])

def all_substrings(s):
 return([s[i: j] for i in range(len(s)) for j in range(i + 1, len(s) + 1)])

def all_substring_palindromes(s):
 pal = []
 for i in all_substrings(s):
  if len(i) < 3:
   continue
  if is_palindrome(i):
   pal.append(i)
 return(pal)

print(max(all_substring_palindromes("bababd"), key = len))